// --- CONFIGURATION ---
// IMPORTANT: Change this to the raw content URL of your GitHub repository.
// Example: 'https://raw.githubusercontent.com/YourUsername/YourRepoName/main/'
const GITHUB_RAW_URL_BASE = 'https://raw.githubusercontent.com/YourUsername/YourRepo/main/';

const contentFiles = {
    'nym-whitepaper': 'whitepaper.md', // Assuming Nym whitepaper is named this
    'quid-whitepaper': 'whitepaper.md', // Assuming QuID whitepaper is named this
    'nomadnet-whitepaper': 'whitepaper.md', // And so on...
    'nym-roadmap': 'roadmap.md',
    'quid-roadmap': 'roadmap.md',
    'nomadnet-roadmap': 'roadmap.md'
};

// --- INITIALIZATION ---
document.addEventListener('DOMContentLoaded', () => {
    // Basic UI Setup
    setupCopyrightYear();
    setupMobileNav();
    setupPageRouting();
    setupScrollAnimations();
    initializeParticles();
    
    // Roadmap specific setup
    setupRoadmapTabs();
});


// --- UI & NAVIGATION ---

function setupCopyrightYear() {
    document.getElementById('copyright-year').textContent = new Date().getFullYear();
}

function setupMobileNav() {
    const toggleBtn = document.querySelector('.mobile-nav-toggle');
    const navLinks = document.querySelector('.nav-links');
    const dropdowns = document.querySelectorAll('.dropdown');

    toggleBtn.addEventListener('click', () => {
        navLinks.classList.toggle('active');
    });

    dropdowns.forEach(dropdown => {
        dropdown.addEventListener('click', (e) => {
            if (window.innerWidth <= 768) {
                // Prevent parent link navigation on mobile
                if (e.target.closest('.dropdown > a')) {
                    e.preventDefault();
                    dropdown.classList.toggle('active');
                }
            }
        });
    });
}

function setupPageRouting() {
    document.body.addEventListener('click', (e) => {
        const routeLink = e.target.closest('.nav-route');
        if (routeLink) {
            e.preventDefault();
            const pageId = routeLink.dataset.page;
            navigateTo(pageId);
        }
    });
}

function navigateTo(pageId) {
    // Hide all pages
    document.querySelectorAll('.page').forEach(page => page.classList.remove('active'));
    
    // Show the target page
    const targetPage = document.getElementById(pageId);
    if (targetPage) {
        targetPage.classList.add('active');
        window.scrollTo(0, 0);

        // Handle content loading for specific pages
        if (pageId.includes('whitepaper')) {
            loadWhitepaper(pageId);
        } else if (pageId === 'roadmap') {
            loadAllRoadmaps();
        }
    }
    
    // Update active state on nav links
    document.querySelectorAll('.nav-route').forEach(link => {
        link.classList.toggle('active', link.dataset.page === pageId);
    });

    // Close mobile nav after selection
    document.querySelector('.nav-links').classList.remove('active');
    document.querySelectorAll('.dropdown').forEach(d => d.classList.remove('active'));
}

// --- CONTENT LOADING (from GitHub) ---

const loadedContent = new Set(); // Cache to prevent re-fetching

async function fetchMarkdown(fileName) {
    const url = GITHUB_RAW_URL_BASE + fileName;
    try {
        const response = await fetch(url);
        if (!response.ok) {
            throw new Error(`HTTP error! status: ${response.status}`);
        }
        return await response.text();
    } catch (error) {
        console.error(`Failed to fetch ${url}:`, error);
        return `# Error\n\nCould not load content for \`${fileName}\`. Please check the file path and repository configuration in \`script.js\`.`;
    }
}

async function loadWhitepaper(pageId) {
    if (loadedContent.has(pageId)) return;

    const bodyEl = document.getElementById(`${pageId}-body`);
    const tocEl = document.getElementById(pageId.replace('whitepaper', 'toc'));
    
    bodyEl.innerHTML = '<p>Loading content...</p>';
    const markdown = await fetchMarkdown(contentFiles[pageId]);
    bodyEl.innerHTML = marked.parse(markdown);
    
    generateToc(bodyEl, tocEl);
    setupTocScrollSpy(bodyEl, tocEl);
    loadedContent.add(pageId);
}

// --- TABLE OF CONTENTS LOGIC ---

function generateToc(contentEl, tocEl) {
    tocEl.innerHTML = '';
    const headings = contentEl.querySelectorAll('h2, h3, h4');
    headings.forEach((heading, index) => {
        const id = `heading-${index}`;
        heading.id = id;
        const link = document.createElement('a');
        link.href = `#${id}`;
        link.textContent = heading.textContent;
        link.classList.add(`toc-${heading.tagName.toLowerCase()}`);
        
        const listItem = document.createElement('li');
        listItem.appendChild(link);
        tocEl.appendChild(listItem);
    });
}

function setupTocScrollSpy(contentEl, tocEl) {
    const headings = Array.from(contentEl.querySelectorAll('h2, h3, h4'));
    const tocLinks = Array.from(tocEl.querySelectorAll('a'));

    const onScroll = () => {
        let activeId = null;
        headings.forEach(heading => {
            const rect = heading.getBoundingClientRect();
            if (rect.top < window.innerHeight / 2) {
                activeId = heading.id;
            }
        });

        tocLinks.forEach(link => {
            link.classList.toggle('active', link.hash === `#${activeId}`);
        });
    };
    
    window.addEventListener('scroll', onScroll, { passive: true });
}

// --- ROADMAP LOGIC ---

function setupRoadmapTabs() {
    const tabs = document.querySelectorAll('.roadmap-tab');
    tabs.forEach(tab => {
        tab.addEventListener('click', () => {
            const tabId = tab.dataset.tab;
            // Switch active tab style
            tabs.forEach(t => t.classList.remove('active'));
            tab.classList.add('active');
            
            // Switch visible content
            document.querySelectorAll('.roadmap-content').forEach(content => {
                content.classList.toggle('active', content.id === tabId);
            });
        });
    });
}

async function loadAllRoadmaps() {
    if (loadedContent.has('all-roadmaps')) return;

    const roadmapIds = ['nym-roadmap', 'quid-roadmap', 'nomadnet-roadmap'];
    for (const id of roadmapIds) {
        const container = document.getElementById(id);
        container.innerHTML = '<p>Loading roadmap...</p>';
        const markdown = await fetchMarkdown(contentFiles[id]);
        container.innerHTML = parseRoadmapMarkdown(markdown);
    }

    loadedContent.add('all-roadmaps');
}

function parseRoadmapMarkdown(markdown) {
    const lines = markdown.split('\n');
    let html = '';
    let inSection = false;
    let inPhase = false;

    lines.forEach(line => {
        if (line.startsWith('## ')) { // Phase
            if (inSection) html += '</ul></div>';
            if (inPhase) html += '</div></details>';
            inSection = false;
            inPhase = true;
            html += `<details class="roadmap-phase" open><summary>${line.substring(3)}</summary><div class="roadmap-phase-body">`;
        } else if (line.startsWith('### ')) { // Section
            if (inSection) html += '</ul></div>';
            inSection = true;
            html += `<div class="roadmap-section"><h4>${line.substring(4)}</h4><ul>`;
        } else if (line.startsWith('- [')) { // Task
            const isComplete = line.startsWith('- [x]');
            const title = line.substring(line.indexOf(']') + 2).replace('âœ…', '').trim();
            const iconClass = isComplete ? 'fa-solid fa-check-circle task-complete' : 'fa-regular fa-circle task-incomplete';
            html += `<li><i class="${iconClass} roadmap-task-status"></i><span class="roadmap-task-title">${title}</span></li>`;
        }
    });

    if (inSection) html += '</ul></div>';
    if (inPhase) html += '</div></details>';
    
    return html;
}

// --- SCROLL ANIMATIONS ---
function setupScrollAnimations() {
    const revealElements = document.querySelectorAll('.reveal');
    const observer = new IntersectionObserver((entries) => {
        entries.forEach(entry => {
            if (entry.isIntersecting) {
                entry.target.classList.add('visible');
            }
        });
    }, { threshold: 0.1 });

    revealElements.forEach(el => observer.observe(el));
}


// --- PARTICLE.JS BACKGROUND ---
function initializeParticles() {
    particlesJS('particles-js', {
        "particles": {
            "number": { "value": 80, "density": { "enable": true, "value_area": 800 } },
            "color": { "value": "#2a3552" },
            "shape": { "type": "circle" },
            "opacity": { "value": 0.5, "random": false },
            "size": { "value": 3, "random": true },
            "line_linked": { "enable": true, "distance": 150, "color": "#2a3552", "opacity": 0.4, "width": 1 },
            "move": { "enable": true, "speed": 2, "direction": "none", "random": false, "straight": false, "out_mode": "out", "bounce": false }
        },
        "interactivity": {
            "detect_on": "canvas",
            "events": { "onhover": { "enable": true, "mode": "repulse" }, "onclick": { "enable": true, "mode": "push" }, "resize": true },
            "modes": { "repulse": { "distance": 100, "duration": 0.4 }, "push": { "particles_nb": 4 } }
        },
        "retina_detect": true
    });
}